/legend>';		$output .= "<table style='width:100%;display:none' id='ci_profiler_session_data'>";		foreach ($dados as $key => $val)		{			if (is_array($val) OR is_object($val))			{				$val = print_r($val, TRUE);			}			$output .= "<tr><td style='padding:5px; vertical-align: top;color:#900;background-color:#ddd;'>".$key."&nbsp;&nbsp;</td><td style='padding:5px; color:#000;background-color:#ddd;'>".htmlspecialchars($val)."</td></tr>\n";		}		$output .= '</table>';		$output .= "</fieldset>";		return $output;       }   }      }