<?phpif (!defined('BASEPATH'))    exit('No direct script access allowed');if (!function_exists('icone_modalidade')) {    function icone_modalidade($modalidade, $nome = null, $somente_nome = null) {        switch ($modalidade) {            case 'futebol':                $retorno = "<i class='fa fa-2x fa-futbol'></i>";                if ($nome) {                    $retorno.=" <span>(Futebol)</span>";                }                if ($somente_nome) {                    $retorno = " Futebol";                }                break;            case 'futebol-americano':                $retorno = "<i class='fa fa-2x fa-football-ball'></i>";                if ($nome) {                    $retorno.=" <span>(Futebol Americano)</span>";                }                if ($somente_nome) {                    $retorno = " Futebol Americano";                }                break;            case 'volei':                $retorno = "<i class='fa fa-2x fa-volleyball-ball'></i>";                if ($nome) {                    $retorno.=" <span>(Vôlei)</span>";                }                if ($somente_nome) {                    $retorno = " Vôlei";                }                break;            case 'basquete':                $retorno = "<i class='fa fa-2x fa-basketball-ball'></i>";                if ($nome) {                    $retorno.=" <span>(Basquete)</span>";                }                if ($somente_nome) {                    $retorno = " Basquete";                }                break;            default:                $retorno = "";                break;        }        return $retorno;    }}if (!function_exists('status_aposta')) {    function status_aposta($jogo) {//ver($jogo);        if ($jogo->Casa_gols_txf == '' && $jogo->Fora_gols_txf == '') {            return "<img class='icone_tabela' data-toggle='tooltip' data-placement='top' title='Aguardando Resultado' src='img/aguardando.png'>";        }        if ($jogo->Casa_gols_txf > $jogo->Fora_gols_txf) {            $coluna = 1;        }        if ($jogo->Casa_gols_txf == $jogo->Fora_gols_txf) {            $coluna = 2;        }        if ($jogo->Fora_gols_txf > $jogo->Casa_gols_txf) {            $coluna = 3;        }        if ($jogo->Palpite[0]) {            if ($jogo->Palpite[0]->Coluna_txf == $coluna) {                return "<img class='icone_tabela' data-toggle='tooltip' data-placement='top' title='Acertou' src='img/acerto.png'>";            } else {                return "<img class='icone_tabela' data-toggle='tooltip' data-placement='top' title='Errou' src='img/erro.png'>";            }        } else {            return "<span class='icone_tabela' data-toggle='tooltip' data-placement='top' title='Não apostou'><i class='fa fa-2x fa-minus-circle'></i><span>";        }    }}if (!function_exists('status_aposta_usuario')) {    function status_aposta_usuario($jogo, $id_usuario) {//ver($jogo);        $palpite = executa_sql("select * from palpites where Jogos_for=$jogo->Id_int and Users_for=$id_usuario");        if ($jogo->Casa_gols_txf == '' && $jogo->Fora_gols_txf == '') {            return "<img class='icone_tabela' data-toggle='tooltip' data-placement='top' title='Aguardando Resultado' src='img/aguardando.png'>";        }        if ($jogo->Casa_gols_txf > $jogo->Fora_gols_txf) {            $coluna = 1;        }        if ($jogo->Casa_gols_txf == $jogo->Fora_gols_txf) {            $coluna = 2;        }        if ($jogo->Fora_gols_txf > $jogo->Casa_gols_txf) {            $coluna = 3;        }        if ($palpite[0]) {            if ($palpite[0]->Coluna_txf == $coluna) {                return "<img class='icone_tabela' data-toggle='tooltip' data-placement='top' title='Acertou' src='img/acerto.png'>";            } else {                return "<img class='icone_tabela' data-toggle='tooltip' data-placement='top' title='Errou' src='img/erro.png'>";            }        } else {            return "<span class='icone_tabela' data-toggle='tooltip' data-placement='top' title='Não apostou'><i class='fa fa-2x fa-minus-circle'></i><span>";        }    }}if (!function_exists('pontos_aposta')) {    function pontos_aposta($jogo) {//ver($jogo);        if ($jogo->Casa_gols_txf == '' && $jogo->Fora_gols_txf == '') {            return 0;        }        if ($jogo->Casa_gols_txf > $jogo->Fora_gols_txf) {            $coluna = 1;        }        if ($jogo->Casa_gols_txf == $jogo->Fora_gols_txf) {            $coluna = 2;        }        if ($jogo->Fora_gols_txf > $jogo->Casa_gols_txf) {            $coluna = 3;        }        if ($jogo->Palpite[0]) {            if ($jogo->Palpite[0]->Coluna_txf == $coluna) {                return 1;            } else {                return 0;            }        }    }}if (!function_exists('pontos_aposta_usuario')) {    function pontos_aposta_usuario($jogo, $id_usuario) {        $palpite = executa_sql("select * from palpites where Jogos_for=$jogo->Id_int and Users_for=$id_usuario");//ver($jogo);        if ($jogo->Casa_gols_txf == '' && $jogo->Fora_gols_txf == '') {            return 0;        }        if ($jogo->Casa_gols_txf > $jogo->Fora_gols_txf) {            $coluna = 1;        }        if ($jogo->Casa_gols_txf == $jogo->Fora_gols_txf) {            $coluna = 2;        }        if ($jogo->Fora_gols_txf > $jogo->Casa_gols_txf) {            $coluna = 3;        }        if ($palpite[0]) {            if ($palpite[0]->Coluna_txf == $coluna) {                return 1;            } else {                return 0;            }        }    }}if (!function_exists('pontos_aposta_admin')) {    function pontos_aposta_admin($jogo, $palpite) {//ver($jogo);        if ($jogo->Casa_gols_txf == '' && $jogo->Fora_gols_txf == '') {            return 0;        }        if ($jogo->Casa_gols_txf > $jogo->Fora_gols_txf) {            $coluna = 1;        }        if ($jogo->Casa_gols_txf == $jogo->Fora_gols_txf) {            $coluna = 2;        }        if ($jogo->Fora_gols_txf > $jogo->Casa_gols_txf) {            $coluna = 3;        }        if ($palpite) {            if ($palpite->Coluna_txf == $coluna) {                return 1;            } else {                return 0;            }        }    }}if (!function_exists('retorna_nome_palpite')) {    function retorna_nome_palpite($palpite = null) {//ver($jogo);        switch ($palpite->Coluna_txf) {            case 1:                return '1';                break;            case 2:                return 'M';                break;            case 3:                return '2';                break;        }    }}if (!function_exists('verifica_liberacao')) {    function verifica_liberacao($rodada = null) {        $date_time = retorna_date_time();        if ($rodada == null) {            die('sem rodada');        }        if ($rodada->Data_inicio_dat <= $date_time) {            if ($rodada->Data_fim_dat >= $date_time) {                return 'ok';            } else {                return 'encerrada';            }        } else {            return 'aguarde';        }    }}