<?phpif (!defined('BASEPATH'))    exit('No direct script access allowed');if (!function_exists('busca_cupons')) {    function busca_cupons($id_instituicao = null, $nome_evento = null, $nome_form = null) {        $CI = & get_instance();        $instituicao = $CI->mbc->executa_sql("select * from instituicoes where Id_int='{$id_instituicao}'");        if (!$instituicao[0]->Id_int) {            return false;        }                        $sql = "select * from formularios_cupons where Evento_sel='{$nome_evento}' and Formulario_sel='{$nome_form}' and Instituicao_sel='{$instituicao[0]->Nome_txf}'";        $formularios_cupons = $CI->mbc->executa_sql($sql);        if (!$formularios_cupons) {            return false;        }        $sql1 = "select * from cupons where Id_objeto_con='{$formularios_cupons[0]->Id_int}' and Utilizado_sel!='SIM'";        $cupons = $CI->mbc->executa_sql($sql1);                return $cupons;    }}