<?phpif (!defined('BASEPATH'))    exit('No direct script access allowed');if (!function_exists('detalhes_imoveis')) {    function detalhes_imoveis($imovel) {        $descricao_imovel = array();        if ($imovel->Tipo_imovel_sel) {            $dado = new stdClass();            $dado->Nome = "Tipo do imóvel:";            $dado->Valor = $imovel->Tipo_imovel_sel;            $descricao_imovel[] = $dado;        }        if ($imovel->Cidade_sel) {            $dado = new stdClass();            $dado->Nome = "Cidade:";            $dado->Valor = $imovel->Cidade_sel;            $descricao_imovel[] = $dado;        }        if ($imovel->Bairro_sel) {            $dado = new stdClass();            $dado->Nome = "Bairro:";            $dado->Valor = $imovel->Bairro_sel;            $descricao_imovel[] = $dado;        }        if ($imovel->Quartos_sel) {            $dado = new stdClass();            $dado->Nome = "Dormitórios:";            $dado->Valor = $imovel->Quartos_sel;            $descricao_imovel[] = $dado;        }        if ($imovel->Suites_sel) {            $dado = new stdClass();            $dado->Nome = "Suítes:";            $dado->Valor = $imovel->Suites_sel;            $descricao_imovel[] = $dado;        }//        if ($imovel->Banheiros_sel) {//            $dado = new stdClass();//            $dado->Nome = "Banheiros:";//            $dado->Valor = $imovel->Banheiros_sel;//            $descricao_imovel[] = $dado;//        }        if ($imovel->Vagas_garagem_sel) {            $dado = new stdClass();            $dado->Nome = "Vagas na garagem:";            $dado->Valor = $imovel->Vagas_garagem_sel;            $descricao_imovel[] = $dado;        }         if ($imovel->Garagem_sel) {            $dado = new stdClass();             $dado->Nome = "Vagas na garagem:";            $dado->Valor = $imovel->Garagem_sel;            $descricao_imovel[] = $dado;        }        if ($imovel->Metragem_txf) {            $dado = new stdClass();            $dado->Nome = "Área Útil:";            $dado->Valor = $imovel->Metragem_txf . " M&sup2;";            $descricao_imovel[] = $dado;        }        if ($imovel->Metragem_total_txf) {            $dado = new stdClass();            $dado->Nome = "Área Total:";            $dado->Valor = $imovel->Metragem_total_txf . " M&sup2;";            $descricao_imovel[] = $dado;        }                //        if ($imovel->Mobiliado_sel) {//            $dado = new stdClass();//            $dado->Nome = "Mobiliado:";//            $dado->Valor = $imovel->Mobiliado_sel;//            $descricao_imovel[] = $dado;//        }                  if ($imovel->Lavabo_sel) {            $dado = new stdClass();            $dado->Nome = "Lavabo Separado:";            $dado->Valor = $imovel->Lavabo_sel;            $descricao_imovel[] = $dado;        }//        if ($imovel->Estuda_proposta_sel) {//            $dado = new stdClass();//            $dado->Nome = "Estuda proposta:";//            $dado->Valor = $imovel->Estuda_proposta_sel;//            $descricao_imovel[] = $dado;//        }//        if ($imovel->Vagas_garagem_sel) {//            $dado = new stdClass();//            $dado->Nome = "Nº de vagas na garagem:";//            $dado->Valor = $imovel->Vagas_garagem_sel;//            $descricao_imovel[] = $dado;//        }        $retorno = new stdClass();        $retorno->caracteristicas = $descricao_imovel;        $retorno->total = count($retorno->caracteristicas);        $retorno->meio=round($retorno->total /2);                return $retorno;    }}