<?phpif (!defined('BASEPATH'))    exit('No direct script access allowed');if (!function_exists('exibe_estoque')) {    function exibe_estoque($estoque = null, $nivel = null) {//          if ($estoque >= 10) {//            $classe = "label-success";//        } elseif ($estoque >= 5) {//            $classe = "label-success";//        } elseif ($estoque >= 1) {//            $classe = "label-laranja";//        } else {//            $classe = "label-danger";//        }                 $classe = "label-default";        if ($nivel == 'administrador') {                                  $valor_estoque = $estoque;        } else {                                                    if ($estoque <= 5) {                $valor_estoque = $estoque;            } else {                $valor_estoque = "005 +";            }        }        $retorno = "<span class='label {$classe}'>{$valor_estoque}</span>";        return $retorno;    }}